import React, { useRef } from "react"
import { Input, Button, InputGroup } from '@chakra-ui/react'
import TodosContext from "./Todos"

function Addtodo() {
    // const inputRef = useRef();
    const {todos, getCurrentTodos} = React.useContext(TodosContext)

    async function handleSubmit(e) {
        if (e.target.value) {
            const item = e.target.value;
            const requestOptions = {
                method: "POST",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify({
                    item: item,
                }),
            }

            await fetch("http://localhost:8000/item", requestOptions);
            getCurrentTodos();
        }
    }

    return (
        <form onSubmit={e => handleSubmit(e)}>
            <InputGroup>
                <Input 
                    placeholder="Enter here"
                    // ref={inputRef}
                    autoFocus={true}
                />
                <Button colorScheme="blue">Sumbit</Button>
            </InputGroup>
        </form>
    )
}

export default Addtodo
