import './App.css';
import { Heading, Box } from "@chakra-ui/react"
import { useState, useEffect } from "react"

function App() {
  const [todos, setTodos] = useState([{"id": 1, "item": "Hello"}])

  // useEffect(() => {
  //   getCurrentTodos
  // })

  return (
    <Box>
      <Heading as="h1" noOfLines={1}>
        Todo List
      </Heading>
      <form id="todo">
        <label for="input">Todo: </label>
        <input
          type="text"
          id="input"
          name="input" 
          placeholder='Enter your input here' 
          />
        <button type="button" form='todo'>Submit</button>
      </form>
      <Box>
        <ul>
          {todos.forEach(element => <li>{element.id}: {element.item}</li>)}
        </ul>
      </Box>
    </Box>


    );
}

export default App;
