import React, { useRef } from "react"
import { Box, Input, Button, InputGroup, Stack, Text, ButtonGroup } from "@chakra-ui/react"
import { useState, useEffect } from "react"

const TodosContext = React.createContext({
    todos: [],
    getCurrentTodos: () => {}
})

function Addtodo() {
    const inputRef = useRef();
    const {todos, getCurrentTodos} = React.useContext(TodosContext)

    async function handleSubmit() {
        if (inputRef.current.value) {
            const newtodo = {
                "id": todos.length + 1,
                "item": inputRef.current.value
            };
            const requestOptions = {
                method: "POST",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify(newtodo),
            }

            await fetch("http://localhost:8000/item", requestOptions);
            getCurrentTodos();
            inputRef.current.value = "";
        }
    }

    return (
            <InputGroup>
                <Input 
                    placeholder="Enter here"
                    ref={inputRef}
                    autoFocus={true}
                />
                <Button colorScheme="blue" onClick={handleSubmit}></Button>
            </InputGroup>
    )
}

function Todo(props){
    const [id, setId] = useState(props.id | null);
    const [item, setItem] = useState(props.item | null);
    const {todos, getCurrentTodos} = React.useContext(TodosContext);

    useEffect(() => {
        setId(props.id);
        setItem(props.item);
    }, [props.id, props.item])

    return (
        <Box>
            <Text fontSize='md'>{id}: {item}</Text>
            <ButtonGroup gap={4}>
                <Button colorScheme='blue' size='sm'>Update</Button>
                <Button colorScheme='red' size='sm'>Delete</Button>
            </ButtonGroup>
        </Box>
    )
}

function Todos(){
    const [todos, setTodos] = useState([])

    async function getCurrentTodos() {
        const response = await fetch("http://localhost:8000/item");
        const data = await response.json();
        setTodos(data.todos);
    }

    useEffect(() => {
      getCurrentTodos()
    }, [])

    return (
        <TodosContext.Provider value={{todos, getCurrentTodos}}>
            <Addtodo />
            <Stack spacing={4}>
                {todos.map((element) => (
                    <Todo id={element.id} item={element.item} />
                ))}
            </Stack>
        </TodosContext.Provider>
    )
}

export default Todos
