import React, { useState } from "react"
import { Flex, Box, Heading, Text, Link, ButtonGroup, Button, Input, Stack, Switch, useColorMode } from "@chakra-ui/react"
import { FormControl, FormLabel, FormHelperText } from "@chakra-ui/react";
import Footer from "./Footer"

function HomePage(props) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailError, setEmailError] = useState(false);
    const [passworError, setPasswordError] = useState(false);

    const { colorMode, toggleColorMode } = useColorMode();
    // const bgColor = { light: "gray.50", dark: "gray.800" };
    const boxBg = { light: "gray.100", dark: "gray.400"}
    const inputBg = { light: "gray.200", dark: "gray.600" };

    async function handleLogin(){
        if (email === "") {
            setEmailError(true);
            return;
        }
        if (password === "") {
            setPasswordError(true);
            return;
        }

        const requestOptions = {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify({
                "email": email,
                "password": password,
            }),
        }
        
        const response = await fetch("http://localhost:8000/user/login", requestOptions);
        if (response.ok){
            data = response.json();
            console.log(data);
        }

    }

    function handleEmailInput(e){
        setEmail(e.target.value);
        if (email !== ""){
            setEmailError(false);
        }
    }

    function handlePasswordInput(e){
        setPassword(e.target.value);
        if (password !== ""){
            setPasswordError(false);
        }
    }

    return (
        <Box>
            <Box p={1} mb={2} bg="gray.400" textAlign="center">
                <Heading as="h1" noOfLines={1}>
                    Todo List
                </Heading>
                <Stack direction="row" spacing={3} justifyContent="center">
                    <Text as='b'>Light</Text>
                    <Switch isChecked={colorMode === 'dark'} onChange={toggleColorMode} />
                    <Text as='b'>Dark</Text>
                </Stack>
            </Box>
            <Flex align="center" justify="center" h="80vh">
                <Box p={5} borderWidth="1px" w="500px" rounded="lg" bg={boxBg[colorMode]}>
                    <Stack spacing={3}>
                        <FormControl>
                            <FormLabel>Email</FormLabel>
                            <Input type='email' 
                                   value={email}
                                   placeholder="Email" 
                                   bg={inputBg[colorMode]} 
                                   onChange={handleEmailInput} />
                            {!emailError ? (null) : (<FormHelperText>Email is required.</FormHelperText>)}
                        </FormControl>
                        <FormControl>
                            <FormLabel>Password</FormLabel>
                            <Input type='password' 
                                   value={password}
                                   placeholder="Password" 
                                   bg={inputBg[colorMode]}  
                                   onChange={handlePasswordInput} />
                            {!passworError ? (null) : (<FormHelperText>Password is required.</FormHelperText>)}
                        </FormControl>
                        <ButtonGroup justifyContent="right">
                            <Button colorScheme="cyan" 
                                    onClick={handleLogin}>Login</Button>
                            <Button colorScheme="green">Sign up</Button>
                        </ButtonGroup>
                    </Stack>       
                </Box>
            </Flex>
            <Footer />
        </Box>
    )
    
}

export default HomePage
