import React from "react"
import {FaGithub, FaFacebook, FaLinkedin} from "react-icons/fa"
import { Box, Text, Link, ButtonGroup } from "@chakra-ui/react"

function Footer() {
    return (
        <Box 
            position="fixed"
            bottom={0}
            left={0}
            width="100%"
            backgroundColor="gray.400"
            p={4}
            textAlign="center"
        >
            <Text>v2.0</Text>
            <Link href='https://github.com/yingtu35' isExternal mr={1}>
                <FaGithub style={{
                            fontSize: "24px",
                            transition: "all 0.3s",
                            }}
                            onMouseEnter={(e) => (e.target.style.fontSize = "36px")}
                            onMouseLeave={(e) => (e.target.style.fontSize = "24px")}
                            />
            </Link>
            <Link href='https://www.linkedin.com/in/yingtu/' color="blue" isExternal>
                <FaLinkedin size="24px" />
            </Link>
        </Box>
    )
}

export default Footer
