import React from "react"
import { useNavigate } from "react-router-dom";
import { MdAccountCircle, MdEmail, MdOutlineLightMode, MdOutlineNightlight } from "react-icons/md"
import { Menu, MenuButton, MenuList, MenuItem, MenuDivider, Button, ButtonGroup, Flex, Text, useColorMode} from "@chakra-ui/react"

function Header(props) {
    const navigate = useNavigate();

    const { colorMode, toggleColorMode } = useColorMode();
    const flexBg = { light: "gray.100", dark: "gray.400"};
    const buttonBg = { light: "gray.200", dark: "gray.600" }

    function handleLogOut() {
        props.logOutCallback();
        navigate('/login');
    }

    return (
        <Flex style={{ display: "grid", justifySelf: "end" }} bg={flexBg[colorMode]}>
            <ButtonGroup justifyContent="right">
                {colorMode === "dark" ?
                <Button size="sm" 
                        leftIcon={<MdOutlineNightlight />} 
                        onClick={toggleColorMode}
                        bg={buttonBg[colorMode]}>
                            Dark Mode
                </Button> 
                :
                <Button size="sm" 
                        leftIcon={<MdOutlineLightMode />} 
                        onClick={toggleColorMode}
                        bg={buttonBg[colorMode]}>
                            Light Mode
                </Button>
                }
                <Button leftIcon={<MdEmail />} size="sm">Message</Button>
                {props.userId?
                <Menu>
                    <MenuButton
                        as={Button}
                        px={4}
                        py={2}
                        transition='all 0.2s'
                        borderRadius='md'
                        borderWidth='1px'
                        size="sm"
                        leftIcon={<MdAccountCircle />}
                        _hover={{ bg: 'gray.200' }}
                        _expanded={{ bg: 'blue.400' }}
                        _focus={{ boxShadow: 'outline' }}
                        
                    >
                    {props.userId}
                    </MenuButton>
                    <MenuList>
                        <Flex align="center" justify="center">
                            <MdAccountCircle size="36px"/>
                            <Text>{props.userId}</Text>
                        </Flex>
                        <MenuItem>Settings</MenuItem>
                        <MenuDivider />
                        <MenuItem onClick={handleLogOut}>Log Out</MenuItem>
                    </MenuList>
                </Menu>
                :
                // <Button leftIcon={<MdAccountCircle />} size="sm">Login</Button>
                (null)}
                
            </ButtonGroup>
        </Flex>

    )
}

export default Header
